        );
    }

    QuestionForm = () => {
        return (
            <div>
                <h1>{decodeMode(this.state.mode)}</h1>
                <form id={"qForm"}>
                    <h1>{this.state.eq}?</h1>
                    <label>
                        Enter Answer Here:
                        <input type={"text"} id={"answer"}/>
                        <input type={"button"} value={"Submit"}/>
                    </label>
                </form>
                <div id={"returnParent"}>
                    <div id={"return"} onClick={(event: React.MouseEvent<HTMLElement>) => {
                        this.clicked(4)
                    }}>
                        Go back
                    </div>
                </div>
                <this.Footer/>
            </div>
        );
    }

    clicked(mode: number) {
        switch (mode) {
            case 0:
                this.easyMode();
                break;
            case 1:
                this.mediumMode();
                break;
            case 2:
                this.hardMode();
                break;
            default:
                break;
        }
        this.setState({
            mode: this.state.mode === 4 ? mode : 4
        })
    }

    render() {
        if (this.state.mode === 4) {
            return (
                <div>
                    <this.HomePage/>
                </div>
            );
        }
        return (
            <div>
                <this.QuestionForm/>
            </div>
        );
    }
}

